import React, { useState, useEffect } from 'react';
import './App.css';
import { Task } from './Task';

function App() {
  const [todolist, setTodoList] = useState([]);
  const [newTask, setNewTask] = useState("");

  const handleChange = (event) => {
    setNewTask(event.target.value);
  };

  const addTask = () => {
    if (newTask.trim() === '') {
      // Don't add task if the new task is empty or contains only whitespace
      return;
    }

    const task = {
      id: todolist.length === 0 ? 1 : todolist[todolist.length - 1].id + 1,
      taskName: newTask,
      completed: false
    };

    setTodoList([...todolist, task]);
    setNewTask(''); // Reset the input field after adding the task
  };

  const deleteTask = (id) => {
    setTodoList(todolist.filter((task) => task.id !== id));
  };

  const completeTask = (id) => {
    setTodoList(
      todolist.map((task) => {
        if (task.id === id) {
          return { ...task, completed: true };
        } else {
          return task;
        }
      })
    );
  };

  return (
    <div className="App">
      <nav className="navbar">
        <h1 className="navbar-heading">ToDo-List</h1>
      </nav>
      <div className="content">
        <div className="container">
          <h1 className="heading">My Todos</h1>
          <div className="add-tasks">
            <input
              type="text"
              value={newTask}
              onChange={handleChange}
              placeholder="Enter a task"
              required
            />
            <button onClick={addTask}>Add Task</button>
          </div>
          <div className="list">
            {todolist.map((task) => (
              <Task
                key={task.id}
                taskName={task.taskName}
                id={task.id}
                deleteTask={deleteTask}
                completeTask={completeTask}
                completed={task.completed}
              />
            ))}
          </div>
          <div className="count">Total Tasks: {todolist.length}</div>
        </div>
      </div>
      <footer className="footer">
        <p>&copy; {new Date().getFullYear()} ToDo-List. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
