import React, { useState, useEffect } from 'react';
import './App.css';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import { Task } from './Task';

function App() {
  const [todolist, setTodoList] = useState(() => {
    const storedTasks = localStorage.getItem('tasks');
    return storedTasks ? JSON.parse(storedTasks) : [];
  });
  const [newTask, setNewTask] = useState('');

  const handleChange = (event) => {
    setNewTask(event.target.value);
  };

  const addTask = () => {
    if (newTask.trim() === '') {
      return;
    }
    const task = {
      id: todolist.length === 0 ? 1 : todolist[todolist.length - 1].id + 1,
      taskName: newTask,
      completed: false,
    };
    setTodoList([...todolist, task]);
    setNewTask('');
  };

  const deleteTask = (id) => {
    setTodoList(todolist.filter((task) => task.id !== id));
  };

  const completeTask = (id) => {
    setTodoList((prevList) => {
      const updatedList = prevList.map((task) => {
        if (task.id === id) {
          return { ...task, completed: true };
        }
        return task;
      });
      return updatedList;
    });
  };

  useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(todolist));
  }, [todolist]);

  const unfinishedTasksCount = todolist.filter((task) => !task.completed).length;
  const finishedTasksCount = todolist.filter((task) => task.completed).length;

  const completedTasks = todolist.filter((task) => task.completed);
  const uncompletedTasks = todolist.filter((task) => !task.completed);

  return (
    <div className="App">
      {/* ...existing code... */}
      <div className="list">
        <TransitionGroup component={null}>
          {uncompletedTasks.map((task) => (
            <CSSTransition
              key={task.id}
              timeout={300}
              classNames={{
                enter: 'fade-enter',
                enterActive: 'fade-enter-active',
                exit: 'fade-exit',
                exitActive: 'fade-exit-active',
              }}
            >
              <Task
                taskName={task.taskName}
                id={task.id}
                deleteTask={deleteTask}
                completeTask={completeTask}
                completed={task.completed}
              />
            </CSSTransition>
          ))}
          {completedTasks.map((task) => (
            <CSSTransition
              key={task.id}
              timeout={300}
              classNames={{
                enter: 'fade-enter',
                enterActive: 'fade-enter-active',
                exit: 'fade-exit',
                exitActive: 'fade-exit-active',
              }}
            >
              <Task
                taskName={task.taskName}
                id={task.id}
                deleteTask={deleteTask}
                completeTask={completeTask}
                completed={task.completed}
              />
            </CSSTransition>
          ))}
        </TransitionGroup>
      </div>
      {/* ...existing code... */}
    </div>
  );
}

export default App;
