export const Task = (props) => {
    const handleCheckboxChange = () => {
      props.completeTask(props.id);
    };

    return (
      <div className="task-container" style={{ backgroundColor: props.completed ? "green" : "white" }}>
        <label className="task-checkbox">
          <input type="checkbox" checked={props.completed} onChange={handleCheckboxChange} />
          <span className="checkmark"></span>
        </label>
        <h1 className={`task-name ${props.completed ? "completed" : ""}`}>{props.taskName}</h1>
        <button className="delete-button" onClick={() => props.deleteTask(props.id)}>X</button>
      </div>
    );
  }
