import { useState } from 'react';
import './App.css';
import {Task} from './Task'

function App() {
  const [todolist, setTodoList] = useState([]);
  const [newTask, setNetTask] = useState("");

  const handleChange = (event) => {
    setNetTask(event.target.value);
  };

  const addTask = () => {
    const task = {
      id: todolist.length === 0 ? 1 : todolist[todolist.length - 1].id + 1,
      taskName: newTask,
      completed : false
    }
    setTodoList([...todolist, task]);
  };

  const deleteTask = (id) => {
    setTodoList(todolist.filter((task) => task.id !== id));
  }

  const completeTask = (id) =>{
    setTodoList(
      todolist.map((task)=>{
        if(task.id === id){
          return {...task,completed:true};
        }
        else{
          return task
        }
      })
    )
  }

  return (
    <div className="App">
      <nav className="navbar">
        <h1 className="navbar-heading">Your Company Name</h1>
      </nav>
      <div className="container">
        <h1 className="heading">My Todos</h1>
        <div className="add-tasks">
          <input
            type="text"
            onChange={handleChange}
            placeholder="Enter a task"
          />
          <button onClick={addTask}>Add Task</button>
        </div>
        <div className="list">
          {todolist.map((task) => {
            return <Task taskName={task.taskName} id={task.id} deleteTask={deleteTask} completeTask={completeTask} completed ={task.completed}/>;
          })}
        </div>
      </div>
      <footer className="footer">
        <p>&copy; {new Date().getFullYear()} Your Company. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
