import { useState } from 'react';
import './App.css';

function App() {
  const [todolist, setTodoList] = useState([]);
  const [newTask, setNetTask] = useState("");

  const handleChange = (event) => {
    setNetTask(event.target.value);
  };

  const addTask = () => {
    const task = {
      id: todolist.length === 0 ? 1 : todolist[todolist.length - 1].id + 1,
      taskName: newTask
    }
    setTodoList([...todolist, task]);
  };

  const deleteTask = (id) => {
    setTodoList(todolist.filter((task) => task.id !== id));
  }

  return (
    <div className="App">
      <div className="container">
        <h1 className="heading">My Todos</h1>
        <ol className="list">
          {todolist.map((task, index) => (
            <li key={task.id} className="task">{`${index + 1}. ${task.taskName}`}
              <button className="delete-button" onClick={() => deleteTask(task.id)}>X</button>
            </li>
          ))}
        </ol>
        <div className="add-tasks">
          <input
            type="text"
            value={taskInput}
            onChange={handleChange}
            placeholder="Enter a task"
          />
          <button onClick={addTask}>Add Task</button>
        </div>
      </div>
    </div>
  );
}

export default App;
